import React, { useState, useEffect } from "react";
import axios from "axios";

const Statistics = () => {
  const [statistics, setStatistics] = useState([]);
  const [dailySessions, setDailySessions] = useState(0);

  useEffect(() => {
    axios
      .get("http://localhost:5000/statistics")
      .then((response) => {
        setStatistics(response.data);
        calculateDailySessions(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

  const calculateDailySessions = (stats) => {
    const today = new Date().toISOString().split("T")[0];
    const todaySessions = stats
      .filter((stat) => stat.date.startsWith(today))
      .reduce((acc, stat) => acc + stat.sessionsCompleted, 0);
    setDailySessions(todaySessions);
  };

  return (
    <div className="statistics">
      <h2>Today:</h2>
      <p>Pomodoro Sessions: {dailySessions}</p>
      <h3>Weekly Summary:</h3>
      <div className="chart">
        {/* Example bar chart data */}
        <div className="bar" style={{ height: "60px" }}></div>
        <div className="bar" style={{ height: "40px" }}></div>
        <div className="bar" style={{ height: "80px" }}></div>
        {/* Add more bars as needed */}
      </div>
      <h3>Monthly Summary:</h3>
      <div className="calendar">{/* Add calendar view here */}</div>
      <h3>Achievements:</h3>
      <div className="achievements">
        <span className="badge">üèÖ Early Bird</span>
        <span className="badge">üèÖ Consistency</span>
        {/* Add more badges as needed */}
      </div>
    </div>
  );
};

export default Statistics;
